    }

    /// 日忌
    /// Daily inauspicious activities (avoids)
    /// - Parameters:
    ///   - month: 月干支
    ///   - day: 日干支
    /// - Returns: 宜忌列表
    public static func getDayAvoids(month: SixtyCycle, day: SixtyCycle) -> [Taboo] {
        return getTaboos(data: DAY_TABOO, supIndex: month.getEarthBranch().getIndex(), subIndex: day.getIndex(), tabooIndex: 1)
    }

    /// 时辰宜
    /// Hourly auspicious activities (recommends)
    /// - Parameters:
    ///   - day: 日干支
    ///   - hour: 时辰干支
    /// - Returns: 宜忌列表
    public static func getHourRecommends(day: SixtyCycle, hour: SixtyCycle) -> [Taboo] {
        return getTaboos(data: HOUR_TABOO, supIndex: hour.getEarthBranch().getIndex(), subIndex: day.getIndex(), tabooIndex: 0)
    }

    /// 时辰忌
    /// Hourly inauspicious activities (avoids)
    /// - Parameters:
    ///   - day: 日干支
    ///   - hour: 时辰干支
    /// - Returns: 宜忌列表
    public static func getHourAvoids(day: SixtyCycle, hour: SixtyCycle) -> [Taboo] {
        return getTaboos(data: HOUR_TABOO, supIndex: hour.getEarthBranch().getIndex(), subIndex: day.getIndex(), tabooIndex: 1)
    }

    private static func getTaboos(data: [String], supIndex: Int, subIndex: Int, tabooIndex: Int) -> [Taboo] {
        // Use components(separatedBy:) to preserve empty strings (equivalent to Java split(";", -1))
        let entries = data[supIndex].components(separatedBy: ";")
        guard subIndex < entries.count else { return [] }
        let parts = entries[subIndex].components(separatedBy: ",")
        guard tabooIndex < parts.count else { return [] }

        let hexData = parts[tabooIndex]
        let luck = Luck.fromIndex(tabooIndex) // 0=吉(宜), 1=凶(忌)
        return parseTabooHexPairs(hexData, luck: luck)
    }

    private static func parseTabooHexPairs(_ hex: String, luck: Luck) -> [Taboo] {
        var taboos: [Taboo] = []
        var i = hex.startIndex
        while i < hex.endIndex {
            guard let next = hex.index(i, offsetBy: 2, limitedBy: hex.endIndex) else { break }
            if let index = Int(String(hex[i..<next]), radix: 16), index < NAMES.count {
                taboos.append(Taboo(name: NAMES[index], luck: luck))
            }
            i = next
        }
        return taboos
    }
}
